void initClock(void)
{
// This is potentially a dangerous function as it could
// result in a system with an invalid clock signal - result: a stuck system
        // Set the PLL up
        // First ensure PLL is disabled
        RCC->CR &= ~(1u<<24);
        while( (RCC->CR & (1 <<25))); // wait for PLL ready to be cleared
        
// Warning here: if system clock is greater than 24MHz then wait-state(s) need to be
// inserted into Flash memory interface
				
        FLASH->ACR |= (1 << 0);
        FLASH->ACR &=~((1u << 2) | (1u<<1));
        // Turn on FLASH prefetch buffer
        FLASH->ACR |= (1 << 4);
        // set PLL multiplier to 12 (yielding 48MHz)
        RCC->CFGR &= ~((1u<<21) | (1u<<20) | (1u<<19) | (1u<<18));
        RCC->CFGR |= ((1<<21) | (1<<19) ); 

        // Need to limit ADC clock to below 14MHz so will change ADC prescaler to 4
        RCC->CFGR |= (1<<14);

        // and turn the PLL back on again
        RCC->CR |= (1<<24);        
        // set PLL as system clock source 
        RCC->CFGR |= (1<<1);
}
void delay(volatile uint32_t dly)
{
	uint32_t end_time = dly + milliseconds;
	while(milliseconds != end_time)
		__asm(" wfi "); // sleep
}

void enablePullUp(GPIO_TypeDef *Port, uint32_t BitNumber)
{
	Port->PUPDR = Port->PUPDR &~(3u << BitNumber*2); // clear pull-up resistor bits
	Port->PUPDR = Port->PUPDR | (1u << BitNumber*2); // set pull-up bit
}
void pinMode(GPIO_TypeDef *Port, uint32_t BitNumber, uint32_t Mode)
{
	/*
	*/
	uint32_t mode_value = Port->MODER;
	Mode = Mode << (2 * BitNumber);
	mode_value = mode_value & ~(3u << (BitNumber * 2));
	mode_value = mode_value | Mode;
	Port->MODER = mode_value;
}
int isInside(uint16_t x1, uint16_t y1, uint16_t w, uint16_t h, uint16_t px, uint16_t py)
{
	// checks to see if point px,py is within the rectange defined by x,y,w,h
	uint16_t x2,y2;
	x2 = x1+w;
	y2 = y1+h;
	int rvalue = 0;
	if ( (px >= x1) && (px <= x2))
	{
		// ok, x constraint met
		if ( (py >= y1) && (py <= y2))
			rvalue = 1;
	}
	return rvalue;
}

void setupIO()
{
	RCC->AHBENR |= (1 << 18) + (1 << 17); // enable Ports A and B
	display_begin();
	pinMode(GPIOB,4,0);
	pinMode(GPIOB,5,0);
	pinMode(GPIOA,8,0);
	pinMode(GPIOA,11,0);
	enablePullUp(GPIOB,4);
	enablePullUp(GPIOB,5);
	enablePullUp(GPIOA,11);
	enablePullUp(GPIOA,8);
}
void updateDisplayTime(void)
{
	static uint32_t lastUpdate = 0;
	uint32_t elapsedTime = millis();

	if(elapsedTime - lastUpdate >= 1000)//update every second
	{
		lastUpdate = elapsedTime;
		static uint32_t seconds = 0;
		seconds++;

		//convert to HH:MS:SS format
		//uint32_t hrs = (seconds / 3600) % 24;
		uint32_t mins = (seconds / 60) % 60;
		uint32_t secs = seconds % 60;

		//convert hrs mins and secs to characters and print
		//printTime(hrs,10,140,0,soilBrown);
		printTime(mins,53,10,0,SOILBROWN);
		printTime(secs,68,10,0,SOILBROWN);
	}
}

//mega efficient random movement algorithm FAANG level.
int mvmt(uint16_t *x, uint16_t *y, uint16_t *oldx, uint16_t *oldy)
{
	// Movement Variables
	int random_mvmt;  // (0=down 1=up)  (2=left 3=right) !Default is 0!
	int random_mvmt_duration;
	
	// Movement
	random_mvmt = 0 + rand() % 5;//randomise direction
	random_mvmt_duration = 5 + rand() % 10;//randomise duration spent in that direction

	///////////////////////START MVMT IF SEQUENCE/////////////////////////
	// Move Down
	if (random_mvmt==0)
	{
		for(int i = 0;i < random_mvmt_duration;i++)
		{
			if (*y < 110)//checks if position is at edge of screen
			{
				*y = *y + 1;	
				fillRectangle(*oldx,*oldy,34,40,SOILBROWN); *oldx= *x; *oldy= *y;
				putImage(*x,*y,34,40,spudman_D1,0,0);
				delay(50);
				putImage(*x,*y,34,40,spudman_D2,0,0);
				delay(50);				
			}
		}//end if(duration)
		return 1;
	}//end if(direction)

	// Move Up
	else if (random_mvmt==1)
	{			
		for(int i = 0;i < random_mvmt_duration;i++)
		{
			if (*y > 20)//checks if position is at edge of screen
			{
				*y = *y - 1;
				fillRectangle(*oldx,*oldy,34,40,SOILBROWN); *oldx=*x; *oldy=*y;
				putImage(*x,*y,34,40,spudman_U1,0,0);
				delay(50);
				putImage(*x,*y,34,40,spudman_U2,0,0);
				delay(50);
			}
		}//end for(duration)
		return 1;
	}//end for(direction)

	// Move Left
	else if (random_mvmt==2)
	{			
		for(int i = 0; i < random_mvmt_duration;i++)
		{
			if (*x > 10)//checks if position is at edge of screen
			{
				*x = *x - 1;
				fillRectangle(*oldx,*oldy,34,40,SOILBROWN); *oldx=*x; *oldy=*y;
				putImage(*x,*y,34,40,spudman_R1,1,0);
				delay(50);
				putImage(*x,*y,34,40,spudman_R2,1,0);
				delay(50);
			}	
		}//end if(duration)	
		return 1;
	}//end if(direction)

	// Move right
	else if (random_mvmt==3) 
	{	
		for(int i = 0; i < random_mvmt_duration;i++)
		{				
			if (*x < 100)//checks if position is at edge of screen
			{
				*x = *x + 1;
				fillRectangle(*oldx,*oldy,34,40,SOILBROWN); *oldx=*x; *oldy=*y;
				putImage(*x,*y,34,40,spudman_R1,0,0);
				delay(50);
				putImage(*x,*y,34,40,spudman_R2,0,0);
				delay(50);
			}
		}//end if(duration)		
		return 1;		
	}//end if(direction)
	else
	{
		//do nothing
		putImage(*x,*y,34,40,spudman_D1,0,0);
		delay(5000);
		return 0;
	}
	///////////////////////END MVMT IF SEQUENCE/////////////////////////
}//end mvmt function

int FunGame(uint16_t *x, uint16_t *y, uint16_t *oldx, uint16_t *oldy)
{
	int score = 0;
	int j = 2;

	while(1)
	{
		fillRectangle(0,0,128,21,GRASSGREEN);
		*x = 5;
		*y = 100;
		putImage(64,120,18,13,slug_1,0,0);
		printNumber(score,80,10,0,SOILBROWN);
		printText("score:",10,10,0,SOILBROWN);
		putImage(*x,*y,34,40,spudman_R1,0,0);

		while(*x < 93)
		{
			*x = *x + j;
			fillRectangle(*oldx,*oldy,34,40,SOILBROWN); *oldx=*x; *oldy=*y;
			putImage(*x,*y,34,40,spudman_R1,0,0);
			putImage(64,120,18,13,slug_1,0,0);
			delay(50);
			putImage(*x,*y,34,40,spudman_R2,0,0);
			putImage(64,120,18,13,slug_2,0,0);
			delay(50);

			//Jump 
			if (movedUp()==0)  
			{
				stopNote();

				for(int i = 0; i < 25;i++)
				{
					if(*x<100)
					{
						*x = *x + 1;
						*y = *y - 1;
					}
					fillRectangle(*oldx,*oldy,34,40,SOILBROWN); *oldx=*x; *oldy=*y;
					putImage(*x,*y,34,40,spudman_R1,0,0);
					putImage(64,120,18,13,slug_1,0,0);
					delay(50);
					putImage(*x,*y,34,40,spudman_R2,0,0);
					putImage(64,120,18,13,slug_2,0,0);
					delay(50);
				}
				for(int i = 0; i < 25;i++)
				{
					if(*x<100)
					{
						*x = *x + 1;
						*y = *y + 1;
					}
					fillRectangle(*oldx,*oldy,34,40,SOILBROWN); *oldx=*x; *oldy=*y;
					putImage(*x,*y,34,40,spudman_R1,0,0);
					putImage(64,120,18,13,slug_1,0,0);
					delay(50);
					putImage(*x,*y,34,40,spudman_R2,0,0);
					putImage(64,120,18,13,slug_2,0,0);
					delay(50);
				}
				delay(100);	
			}//end jump loop
			
			if (isInside(45,100,16,16,*x,*y) || isInside(45,100,16,16,*x+4,*y) || isInside(45,100,16,16,*x,*y+4) || isInside(45,100,16,16,*x+4,*y+4))
			{	
				fillRectangle(0,0,128,160,SOILBROWN); 
				return score;
			}
		}//end for(constant mvmt to the right)
		score += 10;
		if(score > 30)
		{
			j = 4;
		}
		else if(score > 60)
		{
			j = 8;
		}
		
	}//end while(1)
}//end FunGame()

int HungerGame(int hunger, uint16_t *spuddyX, uint16_t *spuddyY, uint16_t *oldSpuddyX, uint16_t *oldSpuddyY ,uint16_t *oldx, uint16_t *oldy)
{	
	fillRectangle(0,0,128,21,GRASSGREEN);
	hunger = 0;
	srand(time(0));//Seed 

	*spuddyX = 50; //Initialize spuddys spawn location and make the pointer remember its last position 
	*spuddyY = 110;
	*oldSpuddyX = *spuddyX;
	*oldSpuddyY = *spuddyY;

	int score = 0;
	int foodX; //initialise food coordinates
	int foodY = 10;
	int food1;

	for(int round = 1; round <=5; round++)
	{
		foodX = 20 + (rand() % (80 - 20 + 1));//generate random x value for food postition 
		foodY = 10;
		food1 = 1;
		*oldx = foodX; //remember foods previous position 
		*oldy = foodY;

		while (food1 == 1)//Activate game
		{
			if(movedLeft()==1 && *spuddyX> 20)//Move spuddy to the left
			{
				*spuddyX -=	5;
				playNote(700);
				delay(300);
				stopNote();
			}
			else if (movedRight()==1 && *spuddyX < 180)//Move spuddy to the right 
			{
				*spuddyX += 5;
				playNote(700);
				delay(300);
				stopNote();
			}

			//Move food down
			foodY += 5; 
			
			fillRectangle(*oldx,*oldy,20,20,SOILBROWN);// clear previous postion of the food and spuddy when they move 
			fillRectangle(*oldSpuddyX,*oldSpuddyY,34,40,SOILBROWN);

			putImage(foodX,foodY,20,20,sun_2,0,0);//Print new position of spuddy
			putImage(*spuddyX,*spuddyY,34,40,spudman_D1,0,0);

			//Store old food positions
			*oldx = foodX;
			*oldy = foodY;
			//Store old spud positions
			*oldSpuddyX = *spuddyX;
			*oldSpuddyY = *spuddyY;
			
			//Makes the food drop down slower 
			delay(200);

			//Check to see if spuddy has collected the food
			if(foodY >= 110)
			{
				if(foodX >= *spuddyX && foodX <= (*spuddyX + 34))
				{
					score++;
					printText("+1",110,10,0,SOILBROWN);
					delay(500);
					fillRectangle(110,10,20,20,SOILBROWN);
				}
				food1 = 0; // Stop game
			}
		}
	}

	//clear screen after game
	fillRectangle(0,0,128,160,SOILBROWN);
		//return hunger value 
		if (score > 1)
		{
			return score = score *20;
		}
		//if no food caught return 0
		else
		{
		return score = 0;
		}
}

